var refractorJavadoclike = require('./javadoclike.js');

module.exports = phpdoc;
phpdoc.displayName = 'phpdoc';
phpdoc.aliases = [];

function phpdoc(Prism) {
  Prism.register(refractorPhp);
  Prism.register(refractorJavadoclike);

