import React,{Component} from 'react'; 
import Styles from "./components/Styles";

class App extends Component { 

    state = { 
      // Initially, no file is selected 
      selectedFile: null,
      detectResults: null
    }; 

    // On file select (from the pop up) 
    onFileChange = event => { 
      // Update the state 
      this.setState({ selectedFile: event.target.files[0] });
    }; 

    // On file upload (click the upload button) 
    onSubmit = () => { 
      const input = document.getElementById("input");
      const output = document.getElementById("output");

      input.addEventListener("change", function () {
        if (this.files && this.files[0]) {
          const myFile = this.files[0];
          const reader = new FileReader();

          reader.addEventListener('load', function (e) {
            output.textContent = e.target.result;
          });

          reader.readAsBinaryString(myFile);
        }
      });
    }; 

    handleSubmit = (event) => {
      event.preventDefault();
    };

    render() { 
      return ( 
        <Styles>
          <form onSubmit={this.handleSubmit}>
            <h1>ğŸ Smart Contract Vulnerability Detection - SCO Demo</h1>
            <a href="https://github.com/erichoang/ge-sc">
              More details
            </a>
            <p>
              This is the quick vulnerability detection demo for <em>reentrancy</em> bug in smart contract.
            </p>
            <div> 
                <input type="file" id="input" onChange={this.onFileChange} />
                <div className="buttons">
                  <button
                    type="submit"
                    onClick={this.onSubmit}
                  >
                    Submit
                  </button>
                </div>
            </div>
            <p>File Name: {this.state.selectedFileName}</p>
            <pre id="output">${output}</pre>
          </form>
        </Styles> 
      ); 
    } 
  } 

  export default App;
