import React,{Component} from 'react';
import { ForceGraph2D} from 'react-force-graph';
export default class Graph extends Component{
    painRing=(node,ctx)=>{
        let gData=this.props.gData;
        let error=new(Array);
        gData.node.array.forEach(node => {
            if(node.error==1) error.add(node.id);
        });
        ctx.beginPath();
        ctx.arc(node.x, node.y, 10 * 1.4, 0, 2 * Math.PI, false);
        ctx.fillStyle = node === error ? 'red' : undefined;
        ctx.fill();
    };
    render(){
        return(
            <ForceGraph2D graphData={this.props.gData} 
            nodeRelSize={10} width={700} height={600}
            nodeCanvasObject={paintRing}
            />
        )
    }
}
