import React,{Component} from 'react'; 
import Styles from "./components/Styles";

class App extends Component { 

    constructor(props){
      super(props);
      this.state = { 
        // Initially, no file is selected 
        selectedFile: null,
        detectResults: null,
        string:''
      }; 

    }

    // On file select (from the pop up) 
    onFileChange = event => { 
      // Update the state 
      this.setState({ selectedFile: event.target.files[0] });
    }
    // On file upload (click the upload button) 
    onSubmit = () => {

      var input = document.querySelector('input[type=file]').files[0];
      var output = document.getElementById("output");

      var reader = new FileReader();
      let i= 0;
      let string = ``;

      reader.onload = function (event) {
        const file = event.target.result;
        const allLines = file.split(/\r\n|\n/);
        // Reading line by line
        allLines.forEach((line) => {
          i +=1
          string += `${i} ${line} \n` 
        })
      };

      reader.onloadend =function(event){
        output.textContent=string
      };

      reader.readAsBinaryString(input); 



    }; 

    handleSubmit = (event) => {
      event.preventDefault();
    };


    render() {

      return ( 
        <Styles>
          <form onSubmit={this.handleSubmit} id="form">
            <h1>üèÅ Smart Contract Vulnerability Detection - SCO Demo</h1>
            <a href="https://github.com/erichoang/ge-sc">
              More details
            </a>
            <p>
              This is the quick vulnerability detection demo for <em>reentrancy</em> bug in smart contract.
            </p>
            <div> 
                <input type="file" id="input" onChange={this.onFileChange} />
                <div className="buttons">
                  <button
                    type="submit"
                    onClick={this.onSubmit}
                  >
                    Submit
                  </button>
                </div>
            </div>
            <p>File Name: {JSON.stringify(this.state.selectedFileName)}</p>
            <pre id="output">{JSON.stringify(this.state.detectResults, null, 4)}</pre>
          </form>

        </Styles> 
      ); 
    } 
  } 

  export default App;
