import React,{Component} from 'react'; 
import Styles from "./components/Styles";

class App extends Component { 

    constructor(props){
      super(props);
      this.state = { 
        // Initially, no file is selected 
        selectedFile: null,
        detectResults: null,
        htmlElement: ""
      }; 

    }

    // On file select (from the pop up) 
    onFileChange = event => { 
      // Update the state 
      this.setState({ selectedFile: event.target.files[0] });
    }
    // On file upload (click the upload button) 
    onSubmit = () => {

      // Create an object of formData 
      const formData = new FormData(); 
      const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json', 'key': 'MqQVfJ6Fq1umZnUI7ZuaycciCjxi3gM0'},
        body: JSON.stringify({ filename: this.state.selectedFile.name})
      };
      fetch('http://localhost:5555/v1.0.0/vulnerability/detection/line', requestOptions)
      .then(response => response.json())
      .then(data => this.setState({ detectResults: data}));

      console.log(this.state.detectResults);

    }; 
    componentDidUpdate(prevProps,prevState){
      if(prevState.detectResults!==this.state.detectResults){
        var input = document.querySelector('input[type=file]').files[0];
        var output = document.getElementById("output");

        var reader = new FileReader();
        let i = 0;
        let string = ``;

        let arrayString = []
        let array = []
        let ArrayUniq = []
        let temp = ``

        var data=this.state.detectResults;

        reader.onload = function (event) {
          const file = event.target.result;
          const allLines = file.split(/\r\n|\n/);
          // Reading line by line

          data["results"].forEach((node, index) => {
            if (node["vulnerability"] == 1) {
              array = [...array, ...node["code_lines"]]
              ArrayUniq = [...new Set(array)];
            }
          })

          allLines.forEach((line) => {
            i += 1
            string = `${i} ${line}`
            arrayString.push(string)
            // console.log('line', line)
          })

          arrayString.forEach((line, index) => {
            if (!ArrayUniq.includes(index)) {
              temp += `<p>${line}</p> \n`
            }
            else {
              temp += `<p class='color-red'>${line}</p>\n`
            }
          })
        };

        reader.onloadend=function(e){
          output.innerHTML=temp
        };
        reader.readAsBinaryString(input);
      }
    };

    handleSubmit = (event) => {
      event.preventDefault();
    };


    render() {

      return ( 
        <Styles>
          <form onSubmit={this.handleSubmit} id="form">
            <h1>üèÅ Smart Contract Vulnerability Detection - SCO Demo</h1>
            <a href="https://github.com/erichoang/ge-sc">
              More details
            </a>
            <p>
              This is the quick vulnerability detection demo for <em>reentrancy</em> bug in smart contract.
            </p>
            <div> 
                <input type="file" id="input" onChange={this.onFileChange} />
                <div className="buttons">
                  <button
                    type="submit"
                    onClick={this.onSubmit}
                  >
                    Submit
                  </button>
                </div>
            </div>
            <p>File Name: {JSON.stringify(this.state.selectedFileName)}</p>
            <pre id="output"></pre>
          </form>

        </Styles> 
      ); 
    } 
  } 

  export default App;
