import React,{Component,useCallback} from 'react';
import { ForceGraph2D} from 'react-force-graph';
export default class Graph extends Component{
    
    render(){
        const paintRing = (node, ctx) => {
            // add ring just for highlighted nodes
            let gData=this.props.gData;
            let error=[];
            gData.nodes.forEach(node => {
                if(node.error===1) error.push(node.id);
            });
            ctx.beginPath();
            ctx.arc(node.x, node.y, 10 * 1.4, 0, 2 * Math.PI, false);
            ctx.fillStyle = error.includes(node.id) ? 'red' : undefined;
            ctx.fill();
          };
    
        return(
            <ForceGraph2D graphData={this.props.gData} 
            nodeRelSize={10} width={700} height={600}
            nodeCanvasObject={paintRing}
            nodeCanvasObjectMode={node => 'before'}
            />
        )
    }
}
